.form {
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	margin: 80px auto;
	font-size: 20px;
	width: 75%;
	border-radius: 10px;
	box-shadow: 0 4px 8px rgb(0, 0, 0, 0.4);
	box-sizing: border-box;
}

.button {
	background-color: #007bff;
	color: #ffffff;
	padding: 10px 20px;
	border: none;
	border-radius: 5px;
	cursor: pointer;
	transition: background-color 0.3s, color 0.3s;
}

.button:hover {
	background-color: #0056b3;
	color: #ffffff;
}

body {
	background-color: #f0f0f0;
}

@media (max-width: 600px) {
	body {
		background-color: #e6f7ff;
	}
}

@media (min-width: 601px) and (max-width: 992px) {
	body {
		background-color: #f9f6ff;
	}
}

@media (min-width: 993px) {
	body {
		background-color: #f2f2f2;
	}
}

.container {
	display: flex;
	flex-direction: column;
	height: 100vh;
}

.header {
	background-color: #333;
	color: #fff;
	padding: 10px;
}

.main-container {
	display: flex;
	flex: 1;
}

.sidebar {
	background-color: #f0f0f0;
	padding: 10px;
	width: 250px;
}

.content {
	flex: 1;
	padding: 10px;
}

.footer {
	background-color: #333;
	color: #fff;
	padding: 10px;
}

.grid-container {
	display: grid;
	grid-template-columns: 1fr 1fr 1fr;
	grid-gap: 10px;
}

.grid-item {
	background-color: #f0f0f0;
	padding: 20px;
	text-align: center;	}

.wrapper {
	border: 2px dotted rgb(96, 139, 168);
	width: 500px;
}

.box {
	display: flex;
	flex-wrap: wrap;
	gap: 10px;
}

.box>* {
	flex: 1 1 160px;
}

.center {
	height: 200px;
	position: relative;
	border: 3px solid green;
}

.center p {
	margin: 0;
	position: absolute;
	top: 50%;
	left: 50%;
	-ms-transform: translate(-50%, -50%);
	transform: translate(-50%, -50%);
}

body {
	font-size: 28px;
}

ul {
	list-style-type: none;
	margin: 0;
	padding: 0;
	overflow: hidden;
	background-color: #333;
	position: -webkit-sticky; /* Safari */
	position: sticky;
	top: 0;
}

li {
	float: left;
}li a {
	display: block;
	color: white;
	text-align: center;
	padding: 14px 16px;
	text-decoration: none;
}

li a:hover {
	background-color: #111;
}

.active {
	background-color: #4CAF50;
}

.dropbtn {background-color: #4CAF50;
	color: white;
	padding: 16px;
	font-size: 16px;
	border: none;
	cursor: pointer;
}

.dropdown {
	position: relative;
	display: inline-block;
}

.dropdown-content {
	display: none;
	position: absolute;
	right: 0;
	background-color: #f9f9f9;
	min-width: 160px;
	box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
	z-index: 1;
}

.dropdown-content a {
	color: black;
	padding: 12px 16px;
	text-decoration: none;
	display: block;
}

.dropdown-content a:hover {background-color: #f1f1f1;}

.dropdown:hover .dropdown-content {
	display: block;
}

.dropdown:hover .dropbtn {
	background-color: #3e8e41;
}

input[type=button], input[type=submit], input[type=reset] {
	background-color: #04AA6D;
	border: none;
	color: white;
	padding: 16px 32px;
	text-decoration: none;
	margin: 4px 2px;
	cursor: pointer;
}

