@media (max-width: 950px) {
	nav .menu {
		display: flex;
	}

nav {
		flex-direction: column;
		align-items: start;
	}

	nav ul {
		display: none;
		flex-direction: column;
		width: 100%;
		margin-bottom: 0.25rem;
	}

